{
    "friendly_name": "twilitransit_scene_1_1_initial_greeting",
    "language": "en",
    "variables": {
        "name": "Alex",
        "dest": "Signal City Market"
    },
    "types": {
        "twilio/text": {
            "body": "Hi {{name}}! Welcome to TwiliTransit! üöé\n\nLet's get you to {{dest}}."
        }
    }
}

{
    "friendly_name": "twilitransit_scene_1_2_trip_plan",
    "language": "en",
    "variables": {
        "name": "Alex",
        "dest": "Signal City Market",
        "start_loc": "TwiliTown Main St. Bus Stop",
        "start_image": "loc-twilitown-bus-stop.jpg",
        "end_loc": "Signal City Market",
        "end_image": "loc-signal-city-market.jpg"
    },
    "types": {
        "twilio/carousel": {
            "body": "Hi {{name}}! Welcome to TwiliTransit! üöé\n\nLet's get you to {{dest}}.",
            "cards": [
                {
                    "title": "Board a bus",
                    "body": "Your trip starts from {{start_loc}}.",
                    "media": "https://forge-assets-5378.twil.io/twilitransit/{{start_image}}",
                    "actions": [
                        {
                            "type": "QUICK_REPLY",
                            "id": "btn_1_2_reminder_bus",
                            "title": "Remind me"
                        }
                    ]
                },
                {
                    "title": "Trip end",
                    "body": "Your trip ends at {{end_loc}}.",
                    "media": "https://forge-assets-5378.twil.io/twilitransit/{{end_image}}",
                    "actions": [
                        {
                            "id": "btn_1_2_email_journey",
                            "title": "Email journey plan",
                            "type": "QUICK_REPLY"
                        }
                    ]
                }
            ]
        },
        "twilio/text": {
            "body": "Hi {{name}}! Welcome to TwiliTransit! üöé\n\nLet's get you to {{dest}}.\n\nYour trip starts from {{start_loc} and ends at {{end_loc}}.\n\nReply with REMIND to get a reminder 5 mins before your bus arrives.\n\nReply with EMAIL to get the journey plan emailed to you instead."
        }
    }
}

{
    "friendly_name": "twilitransit_scene_2_1_reminder",
    "language": "en",
    "variables": {
        "name": "Alex",
        "bus_number": "12A",
        "bus_bay": "2",
        "start_loc": "Main St. Bus Stop"
    },
    "types": {
        "twilio/card": {
            "title": "üöå Bus alert for {{name}}",
            "body": "Your bus (#{{bus_number}}) is arriving in 5 minutes at bus bay {{bus_bay}}.",
            "subtitle": "Let us know once you arrive.",
            "actions": [
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_2_1_yes",
                    "title": "Yes, I'm here"
                },
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_2_1_no",
                    "title": "No, I changed my plans"
                }
            ]
        },
        "twilio/text": {
            "body": "{{name}}, your bus (#{{bus_numer}}) arrives in 5 minutes at bus bay {{bus_bay}}.\n\nReply 'Yes, I'm here' when you arrive at {{start_loc}}  or 'No, I changed my plans' if needed."
        }
    }
}

{
    "friendly_name": "twilitransit_scene_3_1_ferry_transfer",
    "language": "en",
    "variables": {
        "name": "Alex",
        "transfer_window_min": "12",
        "qrcode_image": "qrcode.jpg",
        "ferry_url": "https://maps.app.goo.gl/PJr3ydjoB8vSErCR7"
    },
    "types": {
        "twilio/card": {
            "title": "‚õ¥Ô∏è Ferry transfer for {{name}}",
            "body": "You're almost at the ferry terminal. Transfer window is {{transfer_window_min}} minutes.",
            "media": [
                "https://forge-assets-5378.twil.io/twilitransit/{{qrcode_image}}"
            ],
            "subtitle": "Scan the QR code at the Ferry entrance gate.",
            "actions": [
                {
                    "type": "URL",
                    "id": "btn_3_1_view_directions",
                    "title": "Directions to Ferry",
                    "url": "{{ferry_url}}"
                }
            ]
        },
        "twilio/text": {
            "body": "You're almost at the ferry terminal. Transfer window is {{transfer_window_min}} minutes.\n\nBoarding pass: https://forge-assets-5378.twil.io/twilitransit/{{qrcode_image}}\n\nDirections to Ferry: {{ferry_url}}"
        }
    }
}

{
    "friendly_name": "twilitransit_scene_4_1_metro_transfer",
    "language": "en",
    "variables": {
        "name": "Alex",
        "dest": "Signal City Market",
        "dest_image": "loc-signal-city-market.jpg",
        "platform": "2",
        "transfer_window_min": "6",
        "loc_name": "Riverside Station",
        "loc_image": "loc-riverside-station.jpg",
        "route_map_url": "https://forge-assets-5378.twil.io/twilitransit/routemap.jpg",
        "bikeshare_url": "https://en.wikipedia.org/wiki/Bicycle-sharing_system"
    },
    "types": {
        "twilio/carousel": {
            "body": "Your train will be arrive on Platform #{{platform}}",
            "cards": [
                {
                    "title": "üöá Metro transfer towards {{dest}}",
                    "body": "You're arriving at {{loc_name}}. The connecting train will leave from Platform #{{platform}} in {{transfer_window_min}} minutes.",
                    "media": "https://forge-assets-5378.twil.io/twilitransit/{{loc_image}}",
                    "actions": [
                        {
                            "type": "URL",
                            "id": "btn_4_1_route_map",
                            "title": "View route map",
                            "url": "{{route_map_url}}"
                        }
                    ]
                },
                {
                    "title": "üö¥üèΩ Rent a bike at {{dest}}",
                    "body": "Explore {{dest}} on two wheels using TwiliTransit Bikeshare.",
                    "media": "https://forge-assets-5378.twil.io/twilitransit/{{dest_image}}",
                    "actions": [
                        {
                            "type": "URL",
                            "id": "btn_4_1_rent_bike",
                            "title": "Book your bike",
                            "url": "{{bikeshare_url}}"
                        }
                    ]
                }
            ]
        },
        "twilio/text": {
            "body": "You're arriving at {{loc_name}}. The connecting train to {{dest}} will leave from Platform #{{platform}} in {{transfer_window_min}} minutes.\n\nRoute map: {{route_map_url}}\n\nBook bikeshare at destination: {{bikeshare_url}}"
        }
    }
}

{
    "friendly_name": "twilitransit_scene_4_1_metro_transfer",
    "language": "en",
    "variables": {
        "name": "Alex",
        "trip_summary_pdf": "trip-summary.pdf"
    },
    "types": {
        "twilio/card": {
            "title": "üèÅ Trip summary for {{name}}",
            "body": "You are almost at your destination. Hope you had a pleasant journey.\n\nHow was your experience?",
            "media": [
                "https://forge-assets-5378.twil.io/twilitransit/{{trip_summary_pdf}}"
            ],
            "actions": [
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_5_1_excellent",
                    "title": "Excellent"
                },
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_5_1_average",
                    "title": "Average"
                },
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_5_1_poor",
                    "title": "Poor"
                }
            ]
        },
        "twilio/text": {
            "body": "You are almost at your destination. Hope you had a pleasant journey.\n\nTrip summary: https://forge-assets-5378.twil.io/twilitransit/{{trip_summary_pdf}}\n\nHow was your experience? Reply with Excellent, Average or Poor."
        }
    }
}
